import { useState } from "react";
import filename from "./fileicon.png"

function App() {

  const [file, setfile] = useState("")

    function show(e){

      setfile({
        Name : e.target.files[0].name,
        Size : e.target.files[0].size,
        Type : e.target.files[0].type
      })
      console.log(file)
    }

    function upload(){

      fetch("http://localhost:3001/sendData", {
        method : 'POST',
        headers : {
          "Content-Type": "application/json",
        },
        body : JSON.stringify(file),
      });
    }

  return (
    <div className='file'>
      <p>Upload file</p>
      <label className='box'htmlFor="upload">
      <img src={filename} alt=""/>
        <input type='file' id='upload' size={500} hidden onChange={(e)=> show(e)}/>
        <p className="one">Drop your file or image</p>
        <p className="two">Maximum  file size 500 MP</p>
      </label>
      <button onClick={()=> upload()}>Upload</button>      
      <div className='output'></div>
    </div>
  );
}

export default App;
