import { useState } from "react";
import filename from "./fileicon.png"

function App() {

  const [file, setfile] = useState("")

    function upload(){
      fetch("http://localhost:3001/sendfiles", {
        method: 'post',
        headers: {
          "Content-Type": "application/json",
        },
        body : JSON.stringify(file)
      })
    }
    console.log(file)

  return (
    <div className='file'>
      <p>Upload file</p>
      <label className='box'htmlFor="upload">
      <img src={filename} alt=""/>
        <input type='file' id='upload' size={500} hidden/>
        <p className="one">Drop your file or image</p>
        <p className="two">Maximum  file size 500 MP</p>
      </label>
      <input type="text" onChange={(e)=> setfile(e.target.value)}/>
      <button onClick={()=> upload()}>Upload</button>      
      <div className='output'>{file}</div>
    </div>
  );
}

export default App;
